const FOLLOW = "FOLLOW";
const UNFOLLOW = "UNFOLLOW";

let initialState = {
  users: [
    {
      id: 1,
      fullName: "Natalya",
      status: "I am boss",
      location: { country: "Russia", city: "Izhevsk" },
      followed: true
    },
    {
      id: 2,
      fullName: "Armen",
      status: "I am boss",
      location: { country: "Russia", city: "Izhevsk" },
      followed: false
    },
    {
      id: 3,
      fullName: "Denis",
      status: "I am boss",
      location: { country: "Russia", city: "Izhevsk" },
      followed: true
    },
    {
      id: 4,
      fullName: "David",
      status: "I am boss",
      location: { country: "Russia", city: "Izhevsk" },
      followed: false
    },
    {
      id: 5,
      fullName: "Rudick",
      status: "I am boss",
      location: { country: "Russia", city: "Izhevsk" },
      followed: true
    },
    {
      id: 6,
      fullName: "Maxim",
      status: "I am boss",
      location: { country: "Russia", city: "Izhevsk" },
      followed: true
    }
  ]
};

const usersReducer = (state = initialState, action) => {
  switch (action.type) {
    case FOLLOW: 
        return {
        ...state,
        users: state.users.map( u => {
           if (u.id === action.userId) {
         return {...u, followed: true } }
        return u;})
           }

    case UNFOLLOW: 
      return {
        ...state,
        users: state.users.map( u => {
           if (u.id === action.userId) {
         return {...u, followed: false } }
        return u;})
           }

    default:
      return state;
  }
};
export default usersReducer;
export const followAC = userId => ({ type: FOLLOW, userId });

export const unfollowAC = userId => ({
  type: UNFOLLOW,
  userId
});
