import React from 'react';
import Axios from 'axios';
import avatar from '../../avatar.png';
import classes from './NewUsers.module.css';
const NewUsers = (props) => {
    if(props.users.length === 0) {
        Axios.get("https://social-network.samuraijs.com/api/1.0/users").then(Response => {
            props.setUsers(Response.data.items);
        })
    }
    console.log(props.users);
    debugger;

    return (
        <div className={classes.page_wrapper}>
            {
                props.users.map(user => 
                <div key={user.id} className={classes.user_item}> 
                <img src={avatar} className={classes.avatar}  alt="#" />
                    <div className={classes.user_name}>{user.name}</div>
                    <div className={classes.btn_wrapper}> {user.followed ? <button className={classes.btn} onClick={() => { props.unfollow(user.id) }}>Unfolow</button> : <button onClick={() => { props.follow(user.id) }}>Follow</button>}</div>
                </div>
                )
            }
        </div>
    );
};
export default NewUsers;
